properties.property:
///////////////////////////////////////
// Dicom Data Warehouse (DDW)
// Author: Steve G Langer April 2011
// Purpose: Mirth Dicom channel for the DDW dbase
////////////////////////////////////////
try {
	var func = "mirth:main ";
	logError ("\n\n\n start " + func + DateUtil.getCurrentDate('yyyy-M-d H:m:s'));
	var dbConn = DatabaseConnectionFactory.createDatabaseConnection('org.postgresql.Driver','jdbc:postgresql://localhost:5432/ddw','postgres','postgres');
	//get whole DICOM header, mine them in mapCustomTags()
    var msg = new XML(messageObject.getTransformedData().toString());
	var updatePatient, updateExam, updateSeries, updateAcq, updateInstance, updateAlert ;
	var parmPatient, parmExam, parmSeries, parmAcq, parmInstance, parmAlert ;
	var checkVersion, checkPatient, checkExamUID, checkSeriesUID, checkAcq, checkInstance, checkAlert, chkExamProcess ;
	var parmVersion, parmChkPatient, parmChkExamUID, parmChkSeriesUID, parmCheckAcq, parmCheckInstance, parmCheckAlert, parmChkExamProcess ;
	var Production = true, Archive = false ;

	buildStrings ();

	// Bail if patient info is null
	if ( $('patientId') == '' || $('patientsName') == '') {
		var s = "no_patient> info, aborting. StudyUID = " + $('studyInstanceUid');
		throw s;
	}

	// check if this scanner Version is known
	var result = qryExist (checkVersion, parmVersion);
	if (!result.next()) {
		var s = "unknown_version> on " + $('stationName') + ": " + $('manufacturersModelName') + " " + $('softwareVersions');
		throw s ;
	} else {
		var versionID = result.getString('version_id');
	}

	// Now create patient -only- if patient is not known
	result = qryExist (checkPatient, parmChkPatient) ;
	if (!result.next() ) {
		result = dbaseUpdate (updatePatient, parmPatient);
	}

	// create Exam row -only- if examUID is unknown
	result = qryExist (checkExamUID, parmChkExamUID);
	if (!result.next()){
		result = dbaseUpdate (updateExam, parmExam);
		result = mapCustomTags("exam");	
	}

	// create Series row -only- if seriesUID is unknown
	result = qryExist (checkSeriesUID, parmChkSeriesUID);
	if (!result.next()){
		// We could not know versionID at buildStrings()
		parmSeries.add (versionID );
		result = dbaseUpdate (updateSeries, parmSeries);
		result = mapCustomTags("series");		
	}

	// create Acquisition row -only- if does not exist
	result = qryExist (checkAcq, parmCheckAcq);
	if (!result.next()){
		result = dbaseUpdate (updateAcq, parmAcq);
		result = mapCustomTags("acquisition");
	}

	// create Instance row  -only- if instanceUID is unknown
	result = qryExist (checkInstance, parmCheckInstance);
	if (!result.next()){
		result = dbaseUpdate (updateInstance, parmInstance);
		result = mapCustomTags("instance");
		// this if-block is for the instance binary object table (i.e DOSE SC)
		if ($('modality'.indexOf('CT')) > -1 && ($('imageType').indexOf('SECONDARY')) > -1 ) {
            var jpegs = new DICOMUtil.convertDICOM('jpg', messageObject);
            var data = FileUtil.decode(jpegs);
			parms = new java.util.ArrayList();
			parms.add ($('sopInstanceUid'));
			parms.add ($('studyDate'));
			parms.add (messageObject.getTransformedData());
			parms.add (FileUtil.decode(jpegs));
			dbaseUpdate("INSERT INTO instance_binary_object (instance_uid, date_of_exam, header, content) VALUES (?, ?, ?, ?)", parms);
		} else if (Archive) {
			parms = new java.util.ArrayList();
			parms.add ($('sopInstanceUid'));
			parms.add ($('studyDate'));
			parms.add (messageObject.getTransformedData());
			dbaseUpdate("INSERT INTO instance_binary_object (instance_uid, date_of_exam, header) VALUES (?, ?, ?)", parms);
		}
	}	

	// Last update the Exams_to_Process table
	result = qryExist (chkExamProcess, parmChkExamProcess);
	if (result.next()){
		dbaseUpdate ("DELETE FROM exams_to_process WHERE exam_uid = ? ", parmExamProcess);
	}
	result = dbaseUpdate (updateExamProcess, parmExamProcess);
}
catch (ee) {
	var err_str = "Exception in " + func + "\n" + ee ;
	var alert_type = ee.substring(0, ee.indexOf('>')) ;
	parmCheckAlert.add (alert_type);
	result = qryExist (checkAlert, parmCheckAlert);

	if (Production && result.next() ) {
		// write to logFile, dbase and if urgent email
		logError (err_str + "\n exception end" );
		parmAlert.add (result.getString('alert_type'));
		parmAlert.add (err_str);
		dbaseUpdate (updateAlert, parmAlert);
		if (result.getString('criticality') >1) {
			sendEmail(result.getString('email_to_address'), '', result.getString('email_title'), err_str) ;
		}
	} else {
		logError (err_str + "\n exception end" );
	}
}
finally {
	dbConn.close();
	logError ("normal end " + DateUtil.getCurrentDate('H:m:s'));
}


function buildStrings () {
////////////////////////////////////
// Purpose: Centrallize all SQL here
// Caller: main
/////////////////////////////////////
	func = "mirth:buildStrings ";

	// check version
	checkVersion = "SELECT version_id FROM known_scanners WHERE model ~* ? AND vers ~* ?" ;
	parmVersion = new java.util.ArrayList();
	parmVersion.add( $('manufacturersModelName'));
	parmVersion.add($('softwareVersions').replace('\\','_'));

	// check-update patient
	checkPatient = "SELECT oid FROM patient WHERE mpi_pat_id ~* ? " ;
	parmChkPatient = new java.util.ArrayList();
	parmChkPatient.add( $('patientId'));

	updatePatient = "INSERT INTO patient (pat_name, dob, local_pat_id, mpi_pat_id, gender) VALUES (?,?,?,?,?)";
	parmPatient = new java.util.ArrayList();
	parmPatient.add ($('patientsName'));
	parmPatient.add ($('patientsBirthDate'));
	parmPatient.add ($('patientId'));
	parmPatient.add ($('patientId'));
	parmPatient.add ($('patientsSex'));

	// check-update examUID
	checkExamUID = "SELECT oid FROM exams WHERE exam_uid ~* ? ";
	parmChkExamUID = new java.util.ArrayList();
	parmChkExamUID.add ($('studyInstanceUid'));

	updateExam = "INSERT INTO exams (mpi_pat_id, exam_uid, accession, date_of_exam, time_of_exam, refer_doc, exam_descrip, exam_code, campus, operator, radiologist) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";
	parmExam = new java.util.ArrayList();
	parmExam.add ( $('patientId'));
	parmExam.add ($('studyInstanceUid'));
	parmExam.add ( $('accessionNumber'));
	parmExam.add ( $('studyDate'));
	parmExam.add ($('studyTime') );
	parmExam.add ( $('referringPhysiciansName'));
	parmExam.add ( $('studyDescription'));
	parmExam.add ( '');
	parmExam.add ( $('institutionName'));
	parmExam.add ( $('operatorsName'));
	parmExam.add ( $('nameOfPhysiciansReadingStudy'));

	// check-update seriesUID
	checkSeriesUID = "SELECT oid FROM series WHERE series_uid ~* ? ";
	parmChkSeriesUID = new java.util.ArrayList();
	parmChkSeriesUID.add ($('seriesInstanceUid'));	

	updateSeries = "INSERT INTO series (exam_uid, series_uid, station_id, aet, series_description, protocol_name, series_name, body_part, series_number, version_id) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";
	parmSeries = new java.util.ArrayList();
	parmSeries.add ( $('studyInstanceUid'));
	parmSeries.add ($('seriesInstanceUid') );
	parmSeries.add ( $('stationName'));
	parmSeries.add ( $('sourceApplicationEntityTitle'));
	parmSeries.add ($('seriesDescription') );
	parmSeries.add ( $('protocolName'));
	parmSeries.add ( $('seriesDescription'));
	parmSeries.add ( $('bodyPartExamined'));
	parmSeries.add ( $('seriesNumber'));
	///// VersionID add deferred until call to dbaseUpdate

	// check-update Acquisition
	checkAcq = "SELECT oid FROM acquisition WHERE acq_time ~* ?";
	parmCheckAcq = new java.util.ArrayList();
	parmCheckAcq.add ($('acquisitionTime'));

	updateAcq = "INSERT INTO acquisition (acq_time, acq_number, sop_class, exam_uid, series_uid, event_uid) VALUES (?, ?, ?, ?, ?, ?)";
	parmAcq = new java.util.ArrayList();
	parmAcq.add ($('acquisitionTime'));
	parmAcq.add ($('acquisitionNumber'));
	parmAcq.add ($('sopClassUid'));
	parmAcq.add ($('studyInstanceUid'));
	parmAcq.add ($('seriesInstanceUid'));
	parmAcq.add ('');

	// check-update InstanceUID
	checkInstance = "SELECT oid FROM instance WHERE instance_uid ~* ? ";
	parmCheckInstance = new java.util.ArrayList();
	parmCheckInstance.add ($('sopInstanceUid'));

	updateInstance = "INSERT INTO instance (exam_uid, series_uid, instance_uid, content_time, instance_number, image_type) VALUES (?, ?, ?, ?, ?, ?)";
	parmInstance = new java.util.ArrayList();
	parmInstance.add ($('studyInstanceUid'));
	parmInstance.add ($('seriesInstanceUid'));
	parmInstance.add ($('sopInstanceUid'));
	parmInstance.add ($('contentTime'));
	parmInstance.add ($('instanceNumber'));
	parmInstance.add ( $('imageType'));

	// check-Update Exam_to_Process
	chkExamProcess = "SELECT oid FROM exams_to_process WHERE exam_uid ~* ?";
	parmChkExamProcess = new java.util.ArrayList();
	parmChkExamProcess.add ($('studyInstanceUid'));

	updateExamProcess = "INSERT INTO exams_to_process (exam_uid) VALUES (?)";
	parmExamProcess =  new java.util.ArrayList();
	parmExamProcess.add ($('studyInstanceUid'));

	// check-update Alert_types
	checkAlert = "SELECT * FROM dict_alert_types WHERE alert_type ~* ?";
	parmCheckAlert = new java.util.ArrayList();
	///// defer add until Catch

	updateAlert = "INSERT INTO alerts (exam_uid, mpi_pat_id, date_open, alert_type, message) VALUES (?, ?, ?, ?, ?)";
	parmAlert = new java.util.ArrayList();
	parmAlert.add ($('studyInstanceUid'));
	parmAlert.add ($('patientId'));
	parmAlert.add (DateUtil.getCurrentDate('yyyy-M-d H:m:s'));
	////// other elements deferred until Catch

	return ;
}

function dbaseUpdate (str, params) {
////////////////////////////////////
// Purpose: Centralize dbase updates and
//	error handling
// Caller: Main and mapCustomTags
/////////////////////////////////////
	func= "mirth:dbaseUpdate ";

	logError (str + params);
	var loc_result = dbConn.executeUpdate (str, params);
	// 10-11 Not updateting instance_mapped_values table ...
	//logError (loc_result.next);
	return loc_result;
}


function qryExist (str, param) {
////////////////////////////////////
// Purpose: Centralize dbase qry and error
//	handling
// Caller: Main and mapCustomTags()
/////////////////////////////////////
	func = "mirth:qryExist ";

	//logError (str + param);
	var loc_result = dbConn.executeCachedQuery(str, param);
	return loc_result;
}


function mapCustomTags (scope) {
/////////////////////////////////////////////
// Purpose: This is the secret sauce that
//	enables indexing arbitrary tags per Version
//	w/out new Tables/Columns or NoSQL
// Caller: Exam/Series/Instance update Blocks in main
/////////////////////////////////////////////
	func = "mirth:mapCustomTags ";
	var strUpdate, uid ;
	var parmUpdate = new java.util.ArrayList();

	var strQry = "SELECT std_name, unit, dicom_grp_ele FROM mapp_view WHERE scope ~* ? AND version_id = ?";
	var parmQry = new java.util.ArrayList();
	parmQry.add (scope);
	parmQry.add (parseInt(versionID)); //does this need parseInt now that it's not an oid?

	if (scope == 'exam') {
		strUpdate = "INSERT INTO exams_mapped_values (exam_uid, std_name, value, unit) VALUES (?, ?, ?, ?)";
		uid = $('studyInstanceUid') ;
	} else if (scope == 'series') {
		strUpdate = "INSERT INTO series_mapped_values (series_uid, std_name, value, unit) VALUES (?, ?, ?, ?)";
		uid = $('seriesInstanceUid') ;
	} else if (scope == 'acquisition') {
		strUpdate = "INSERT INTO acquisition_mapped_values (event_uid, std_name, value, unit) VALUES (?, ?, ?, ?)";
		uid = $('') ;
	} else if (scope == 'instance') {
		strUpdate = "INSERT INTO instance_mapped_values (instance_uid, std_name, value, unit) VALUES (?, ?, ?, ?)";
		uid = $('sopInstanceUid') ;
	}

	// need a View linking stdNames to mappedValues
	// then Qry the View for the list of custom tags
	// Loop over the result set, and pluck named tags from
	// -var msg- defined in the try block
	result = qryExist (strQry, parmQry) ;
	while (result.next() ) {
		parmUpdate.add (uid);
		parmUpdate.add (result.getString('std_name'));
		parmUpdate.add (msg[result.getString('dicom_grp_ele')].toString());		
		parmUpdate.add (result.getString('unit'));
		dbaseUpdate (strUpdate, parmUpdate);
		parmUpdate.clear();
	}
}


function logError (str) {
////////////////////////////////////
// Purpose: Central output for debug messaging
// Caller: throughout this code
/////////////////////////////////////
	str = str + "\n" ;
   	FileUtil.write('c:\\error_images\\log.rtf', true, str);
	return ;
}


function sendEmail(to, cc, title, content){
////////////////////////////////////
// Purpose: For Exceptions in Prod, this
//		emails ALerts to "to"
// Caller: Catch
/////////////////////////////////////
   var host = 'smtprelay.mayo.edu';
   var port = '25' , auth = false, secure = '', password = '';
   var username = 'dlradtrac@mayo.edu', from = 'dlradtrac@mayo.edu';
   var smtpConn = new SMTPConnection(host, port, auth, secure, username, password);
   smtpConn.send(to, cc, from, title, content);
}

